import React from "react";
import "./ThemeSelector.css";

const themes = [
  {
    name: "Minimalist",
    description: "Simple and clean design for portfolios.",
    color: "#F5F5F5",
  },
  {
    name: "Bold & Vibrant",
    description: "Bright colors and big typography.",
    color: "#FFD700",
  },
  {
    name: "Dark Mode",
    description: "Sleek, dark design to reduce eye strain.",
    color: "#2C2C2C",
  },
  {
    name: "Pastel Pop",
    description: "Soft pastel tones and playful layout.",
    color: "#FFB6C1",
  },
];

function ThemeSelector() {
  return (
    <div className="theme-wrapper">
      <div className="theme-container">
        <div className="theme-header">
          <h1>Choose a Theme</h1>
          <p>Select one that suits your project</p>
        </div>
        <div className="theme-grid">
          {themes.map((theme, index) => (
            <div
              key={index}
              className="theme-card"
              style={{ backgroundColor: theme.color }}
            >
              <h2>{theme.name}</h2>
              <p>{theme.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ThemeSelector;
