import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css'; // Make sure this CSS file exists for styling

function LandingPage() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [theme, setTheme] = useState('default');
  const navigate = useNavigate(); // Hook for navigation

  const slides = [
    'https://www.adamenfroy.com/wp-content/uploads/AdobeStock_485691272.jpeg',
    'https://images.bisnis.com/upload/img/pekerjaan_freelance.png',
    'https://www.liveabout.com/thmb/dRmwxrDkQQffvSppdRQTA5h5ZnI=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/artist-working-on-painting-concentrating-in-studio--937161320-5c8f067546e0fb00016ee0e0.jpg',
    'https://www.careergirls.org/wp-content/uploads/2018/05/FashionDesigner_1920x1080.jpg',
    'https://tuhoraonline.com/wp-content/uploads/2021/04/freelancer.jpg',
  ];

  // Handle SignUp Click
  const handleSignUpClick = () => {
    navigate('/signup'); // Navigate to the signup page
  };

  // Handle Login Click
  const handleLoginClick = () => {
    navigate('/login'); // Navigate to the login page
  };

  // Next Slide function
  const handleNextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
  };

  // Handle Theme Change
  const handleThemeChange = (event) => {
    setTheme(event.target.value);
  };

  // Apply theme when theme state changes
  useEffect(() => {
    const root = document.documentElement.style;
    const themeVars = {
      bakery: ['#F1C27D', '#D2691E'],
      tech: ['#4A90E2', '#FF6F61'],
      clothing: ['#C71585', '#FFD700'],
      handmade: ['#8A2BE2', '#FFD700'],
      default: ['#0d6efd', '#ffc107'],
    };

    root.setProperty('--primary-color', themeVars[theme][0]);
    root.setProperty('--accent-color', themeVars[theme][1]);
  }, [theme]);

  useEffect(() => {
    const slideInterval = setInterval(handleNextSlide, 5000);
    return () => clearInterval(slideInterval);
  }, []);

  return (
    <nav className="landing-nav">
  <div className="nav-left">
    <div className="logo">
      <i className="fas fa-brain"></i>
      <span>AI Tools</span>
    </div>
  </div>
  <div className="nav-right">
    <a href="#about" className="nav-link">About</a>

    {/* Theme dropdown */}
    <select id="themeSwitcher" onChange={handleThemeChange} value={theme} className="theme-dropdown">
      <option value="default">Default</option>
      <option value="bakery">Bakery</option>
      <option value="tech">Tech</option>
      <option value="clothing">Clothing</option>
      <option value="handmade">Handmade</option>
    </select>

    {/* Auth buttons */}
    <button className="auth-btn login-btn" onClick={handleLoginClick}>Login</button>
    <button className="auth-btn signup-btn" onClick={handleSignUpClick}>Sign Up</button>
  </div>
</nav>

  );
}

export default LandingPage;
